mapper.select:
select * from (select * from ( SELECT rank_game.st_no, stadium_info.stadium, stadium_info.location, rank_game.gametime, rank_game.g_status, rank_game.managerid , rank_game.manager FROM rank_game JOIN stadium_info ON stadium_info.st_no = rank_game.st_no WHERE rank_game.managerid = #{managerid} And rank_game.gametime > sysdate
) where rownum<=${onePageRecord*nowPage} order by st_no asc
)
where rownum<=${onePageRecord} order by st_no desc
where rownum<=${onePageRecord} order by st_no desc

select * from (select * from (
select rank_game.s_no, rank_game.no, stadium_info.stadium, stadium_info.location, rank_game.gametime, rank_game.g_status, rank_game.managerid, rank_game.manager from rank_game join stadium_info on stadium_info.st_no = rank_game.st_no WHERE rank_game.managerid = #{managerid} order by rank_game.no desc
) where rownum<=${onePageRecord*nowPage} order by no asc
)
where rownum<=${onePageRecord} order by no desc
where rownum<=${onePageRecord} order by no desc

