mapper.select:
SELECT * FROM ( SELECT rownum rnum, t.* FROM ( SELECT s.stadium, rg.gametime, ms.d_status, ms.amount FROM rank_game rg INNER JOIN stadium_info s ON rg.st_no = s.st_no INNER JOIN m_settlement ms ON rg.no = ms.no WHERE 1=1
AND s.stadium LIKE '%' || #{keyword} || '%'
AND TO_DATE(rg.gametime, 'YY/MM/DD') = #{datetime}
ORDER BY
rg.gametime DESC ms.d_status DESC rg.gametime DESC
) t WHERE rownum <= ${pageNum * amount} ) WHERE rnum > ${(pageNum - 1) * amount }

SELECT count(*) FROM rank_game rg INNER JOIN stadium_info s ON rg.st_no = s.st_no INNER JOIN m_settlement ms ON rg.no = ms.no WHERE 1=1
AND s.stadium LIKE '%'||#{keyword}||'%'
AND TO_DATE(rg.gametime, 'YY/MM/DD') = #{datetime}
